# Custom CRC16 instruction
def crc16_update(crc, byte):
    crc ^= byte
    for _ in range(8):
        if crc & 1:
            crc = (crc >> 1) ^ 0xA001
        else:
            crc >>= 1
    return crc

rd_val = read_register(operands['rd'])
rs2_val = read_register(operands['rs2'])
result = crc16_update(rd_val, rs2_val & 0xFF)
write_register(operands['rd'], result)
