c0: .byte 0x00
c1: .byte 0xFF

.text
main:
    #place padel 
    lui t0, 0xF0
    slli t0, 1
    li t1, 1
    sb t1, 0(t0)

    loop:
    call moveball
    j loop

moveball:
        li a0, 0        #remove ball tile
        call updateBallTile

        la t0, path
        lb s0, 0(t0)
        
    #path 0 -> right
        bnz s0, path1

        addi t0, 1
        addi t1, 0

        j update_coo

    #path 1 -> left
    path1:
        li t0, 1
        bne s0, t0, path2

        addi t0, -1
        addi t1, 0

        j update_coo

    #path 2 -> right down
    path2:
        li t0, 2
        bne s0, t0, path3

        addi t0, 1
        addi t1, -1

        j update_coo

    #path 3 -> left down
    path3:
        li t0, 3
        bne s0, t0, path4

        addi t0, -1
        addi t1, -1

        j update_coo

    #path 4 -> right up
    path4:
        li t0, 4
        bne s0, t0, path5

        addi t0, 1
        addi t1, 1

        j update_coo

    #path 5 -> left up
    path5:
        addi t0, -1
        addi t1, 1

    update_coo:
        la s0, yp
        lb s1, 0(s0)
        add s1, t1
        sb s1, 0(s0)
        mv s0, s1       #save new y coordinates in s0

        la t1, xp
        lb s1, 0(t1)
        add s1, t0
        sb s1, 0(t1)    #save new x coordinates in s1

        li a0, 1        #put ball tile
        call updateBallTile
    ret

updateBallTile:
        #load y and x into s0, s1
        la t0, yp
        lb s0, 0(t0)
        
        la t0, xp
        lb s1, 0(t0)

        la t0, Row0    #t0 = 0xF000
    multiply:          #t0 += 20*y
        bz s0, add
        addi t0, 20
        addi s0, -1
        j multiply
    add:
        add t0, s1      #t0 += x
        sb a0, 0(t0)    #store argument
    ret