c0: .word 0x00FF


.text


reset:
    #check conditions of game -> score
    la t0, scoreA
    lb s0, 0(t0)    #s0 = scoreA

    li t1, 10
    #beq s0, t1, endgame

    addi t0, 1
    lb s0, 0(t0)    #s0 = scoreB
    #beq s0, t1, endgame

    #put ball tile
    li a0, 1        
    call updateBallTile 

    #put padels
    li a0, 2        
    call update_pileTile

    #check for w press
    li a0, 63
    addi a0, 56
start:
    ecall 7
    bz a1, start
endgame:
    ecall 10


updateBallTile:
    #load y and x into s0, s1   
        lui t0, 0xF1        #y -> 0xF131
        slli t0, 1
        ori t0, 0x31
        lb s1, 0(t0)

        addi t0, -1         #x -> 0xF130
        lb s0, 0(t0)

        lui t0, 0xF0
        slli t0, 1    #t0 = 0xF000
    
        slli s0, 2    # y*4
        add t0, s0    # t0 += y*4
        slli s0, 2    # y*16
        add t0, s0    # t0 += y*16
        add t0, s1    # t0 += x
        sb a0, 0(t0)

update_pileTile:
        
        #padel A
        li s1, 0    #s0 = x = 0

        #load y into s0  
        lui t1, 0xF1        
        slli t1, 1
        ori t1, 0x2E
        lb s0, 0(t1)

        li t0, 2
        bne a0, t0, link
        li s0, 8                #set padel A to 8
        sb s0, 0 (t1)
        j rest
link:
    j continue
rest:
        addi t1, 1
        lb s1, 0(t1)
        li s1, 8                #set padel B to 8
        sb s1, 0 (t1)
        addi t1, -1             #undo changes to t1
        
continue:
        lui t0, 0xF0
        slli t0, 1    #t0 = 0xF000
    
        slli s0, 2    # y*4
        add t0, s0    # t0 += y*4
        slli s0, 2    # y*16
        add t0, s0    # t0 += y*16
        add t0, s1    # t0 += x
        sb a0, 0(t0)

        #padel B
        li s1, 19    #s0 = x = 19

        #load y into s0  
        addi t1, 1
        lb s0, 0(t1)

        lui t0, 0xF0
        slli t0, 1    #t0 = 0xF000
    
        slli s0, 2    # y*4
        add t0, s0    # t0 += y*4
        slli s0, 2    # y*16
        add t0, s0    # t0 += y*16
        add t0, s1    # t0 += x
        sb a0, 0(t0)

    ret
